
// Define types for popular image formats

// JPEG
declare module '*.jpg' {
    const value: string;
    export default value;
}

declare module '*.jpeg' {
    const value: string;
    export default value;
}

// PNG
declare module '*.png' {
    const value: string;
    export default value;
}

// GIF
declare module '*.gif' {
    const value: string;
    export default value;
}

// SVG
declare module '*.svg' {
    const value: string;
    export default value;
}

// SVG
declare module '*.webp' {
    const value: string;
    export default value;
}